mapper.select:
select 
from t_file
where path=#{homePath} and owner=#{account}

mapper.update:
update t_file
set parent_id = #{parentId},
    path = #{path}
where file_id = #{fileId}

update t_file
set trash = 1, delete_time = #{deleteTime}
where file_id = #{fileId}

update t_file
set trash = 2, delete_time = #{deleteTime}
where path like concat(#{path},"%")

update t_file
set trash = 0, delete_time = null
where file_id = #{fileId}
